import "./App.css";
import { useState } from "react";
import Axios from "axios";
import Item from "./components/item.jsx";

function App() {
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState(0);
  const [unitValue, setUnitValue] = useState(0);

  const [itemsList, setItemsList] = useState([]);

  const createItem = () => {
    Axios.post("http://localhost:3001/create", {
      name: name,
      quantity: quantity,
      unitValue: unitValue,
    })
      .then((response) => {
        getItems();
      })
      .catch((err) => {
        console.log(err.toString());
      });
  };

  const getItems = () => {
    Axios.get("http://localhost:3001/items")
      .then((response) => {
        setItemsList(response.data);
      })
      .catch((err) => {
        console.log(err.toString());
      });
  };

  return (
    <div className="App">
      <div className="header">
        <h1>LogistX Inventory Tracker</h1>
      </div>

      <h3 className="subheader">Add Inventory Items</h3>
      <div className="divider"></div>
      <div className="inputs">
        <label>
          Name
          <input
            type="text"
            onChange={(event) => setName(event.target.value)}
          />
        </label>
        <label>
          Quantity
          <input
            type="number"
            onChange={(event) => setQuantity(event.target.value)}
          />
        </label>
        <label>
          Unit Value
          <input
            type="number"
            onChange={(event) => setUnitValue(event.target.value)}
          />
        </label>
        <button className="large-button" onClick={createItem}>
          Add Item
        </button>
      </div>
      <h3 className="subheader">Manage Inventory</h3>
      <div className="divider"></div>
      <div className="inventory">
        <button className="large-button" onClick={getItems}>
          Load Inventory
        </button>
        <div className="columns">
          <h4>Item ID</h4>
          <h4>Name</h4>
          <h4>Quantity</h4>
          <h4>Unit Price</h4>
          <h4>Last Update</h4>
          <h4></h4>
          <h4></h4>
        </div>
        {itemsList.map((val, key) => {
          return (
            <Item
              key={val.id}
              id={val.id}
              name={val.name}
              quantity={val.quantity}
              unitValue={val.unitValue}
              lastUpdate={val.updatedAt.substring(0, 10)}
              refresh={getItems}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
