import "./App.css";
import { useState } from "react";
import Axios from "axios";
import Item from "./components/Item.jsx";
import InventoryForm from "./components/InventoryForm";

function App() {
  const [itemsList, setItemsList] = useState([]);

  // REMOVE

  useEffect(() => {
    resetValues();
  }, []);

  const resetValues = async () => {
    const items = [
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
      { name: "", quantity: "", unitValue: "", location: "" },
    ];

    const locations = [
      { name: "Warehouse 1", city: "Toronto", address: "123 Shopify St." },
      { name: "Warehouse 2", city: "Mississauga", address: "345 Merchant Rd." },
      { name: "Warehouse 3", city: "London", address: "8 Commerce Ave." },
    ];

    await getItems();
    itemsList.forEach((item) => {
      Axios.delete("http://localhost:3001/deleteItem", {
        data: {
          id: item.id,
        },
      });
    });

    items.forEach((item) => {
      createItem(item.name, item.quantity, item.unitValue, item.location);
    });

    const locList = await getLocations();
    locList.forEach((loc) => {
      Axios.delete("http://localhost:3001/deleteLocation", {
        data: {
          id: loc.id,
        },
      });
    });

    locations.forEach((loc) => {
      createLocation(loc.name, loc.city, loc.address);
    });
  };

  const createItem = (name, quantity, unitValue, location) => {
    Axios.post("http://localhost:3001/createItem", {
      name: name,
      quantity: quantity,
      unitValue: unitValue,
      location: location,
    })
      .then((response) => {
        getItems();
      })
      .catch((err) => {
        console.log(err.toString());
      });
  };

  const getLocations = () => {
    Axios.get("http://localhost:3001/getLocations").then((response) => {
      return response.data;
    });
  };

  const createLocation = (name, city, address) => {
    Axios.post("http://localhost:3001/createLocation", {
      name: name,
      city: city,
      address: address,
    });
  };

  // REMOVE
  const getItems = () => {
    Axios.get("http://localhost:3001/getItems")
      .then((response) => {
        setItemsList(response.data);
      })
      .catch((err) => {
        console.log(err.toString());
      });
  };

  return (
    <div className="App">
      <div className="header">
        <h1>LogistX Inventory Tracker</h1>
      </div>
      <InventoryForm getItems={getItems} />
      <h3 className="subheader">Manage Inventory</h3>
      <div className="divider"></div>
      <div className="inventory">
        <button className="large-button" onClick={getItems}>
          Load Inventory
        </button>
        {itemsList.length == 0 ? (
          <div></div>
        ) : (
          <div className="columns">
            <h4>Item ID</h4>
            <h4>Name</h4>
            <h4>Quantity</h4>
            <h4>Unit Price</h4>
            <h4>Last Update</h4>
            <h4></h4>
            <h4></h4>
          </div>
        )}
        {itemsList.map((val, key) => {
          return (
            <Item
              key={val.id}
              id={val.id}
              name={val.name}
              quantity={val.quantity}
              unitValue={val.unitValue}
              location={val.location}
              lastUpdate={val.updatedAt.substring(0, 10)}
              refresh={getItems}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
