import "./components.css";
import { useState, locationList } from "react";
import Axios from "axios";
import PropTypes from "prop-types";

function InventoryForm(props) {
  const [itemName, setItemName] = useState("");
  const [quantity, setQuantity] = useState(0);
  const [unitValue, setUnitValue] = useState(0);
  const [location, setLocation] = useState(initialState);
  const [locationList, setLocationList] = useState(initialState);

  useEffect(() => {
    getLocations().then((locations) => {
      setLocationList(locations);
    });
  }, []);

  const [locationName, setLocationName] = useState("");
  const [city, setCity] = useState("");
  const [address, setAddress] = useState("");

  const getLocations = () => {
    Axios.get("http://localhost:3001/getLocations")
      .then((response) => {
        setLocationList(response.data);
      })
      .catch((err) => {
        console.log(err.toString());
      });
  };

  const createLocation = (name, city, address) => {
    Axios.post("http://localhost:3001/createItem", {
      name: name,
      city: city,
      address: address,
    });
  };

  return (
    <div className="row">
      <div className="inputs">
        <h3 className="subheader">Add Inventory Items</h3>
        <div className="subdivider"></div>
        <label>
          Name
          <input
            type="text"
            onChange={(event) => setItemName(event.target.value)}
          />
        </label>
        <label>
          Quantity
          <input
            type="number"
            onChange={(event) => setQuantity(event.target.value)}
          />
        </label>
        <label>
          Unit Value
          <input
            type="number"
            onChange={(event) => setUnitValue(event.target.value)}
          />
        </label>
        <label>
          Location
          <select onChange={(event) => setUnitValue(event.target.value)}>
            {locationsList.map((val, key) => {
              return <option value={val.name}>{val.name}</option>;
            })}
          </select>
        </label>
        <button
          className="large-button"
          onClick={() => {
            props.createItem(itemName, quantity, unitValue);
          }}
        >
          Add Item
        </button>
      </div>
      <div className="inputs">
        <h3 className="subheader">Add Location</h3>
        <div className="subdivider"></div>
        <label>
          Name
          <input
            type="text"
            onChange={(event) => setLocationName(event.target.value)}
          />
        </label>
        <label>
          City
          <input
            type="number"
            onChange={(event) => setNumber(event.target.value)}
          />
        </label>
        <label>
          Address
          <input
            type="number"
            onChange={(event) => setAddress(event.target.value)}
          />
        </label>
        <button
          className="large-button"
          onClick={() => {
            createLocation(locationName, city, address);
          }}
        >
          Add Location
        </button>
      </div>
    </div>
  );
}

InventoryForm.propTypes = {
  createItem: PropTypes.func,
};

export default InventoryForm;
