import React from "react";
import PropTypes from "prop-types";
import "./components.css";
import { useState } from "react";

function Item(props) {
  const [ID, setID] = useState(props.id);
  const [name, setName] = useState(props.name);
  const [quantity, setQuantity] = useState(props.quantity);
  const [unitValue, setUnitValue] = useState(props.unitValue);
  const [edit, setEdit] = useState(false);

  return edit ? (
    <div className="item">
      <h4 className="field">{props.id}</h4>
      <input type="text" placeholder="Name" value={props.name}></input>
      <input
        type="number"
        placeholder="Quantity"
        value={props.quantity}
      ></input>
      <input
        type="number"
        placeholder="Unit Value"
        value={props.unitValue}
      ></input>
      <button className="edit" onClick={setEdit(!edit)}>
        Save
      </button>
    </div>
  ) : (
    <div className="item">
      <h4 className="field">{props.id}</h4>
      <h4 className="field">{props.name}</h4>
      <h4 className="field">{props.quantity}</h4>
      <h4 className="field">{props.unitValue}</h4>
      <button className="edit" onClick={setEdit(!edit)}>
        Edit
      </button>
      <button className="delete">Delete</button>
    </div>
  );
}

Item.propTypes = {
  id: PropTypes.number,
  name: PropTypes.string,
  quantity: PropTypes.number,
  unitValue: PropTypes.number,
};

export default Item;
