import React from "react";
import PropTypes from "prop-types";
import "./components.css";
import Axios from "axios";
import { useState, setState } from "react";

function Item(props) {
  const [id, setID] = useState(props.id);
  const [name, setName] = useState(props.name);
  const [quantity, setQuantity] = useState(props.quantity);
  const [unitValue, setUnitValue] = useState(props.unitValue);
  const [edit, setEdit] = useState(false);

  const updateItem = () => {
    Axios.post("http://localhost:3001/update", {
      id: id,
      name: name,
      quantity: quantity,
      unitValue: unitValue,
    })
      .then(() => {
        console.log("Successfully updated iventory item");
      })
      .catch((err) => {
        console.log(err.toString());
      });
  };

  const deleteItem = () => {};

  return edit ? (
    <div className="item">
      <h4 className="field">{props.id}</h4>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(event) => setName(event.target.value)}
      ></input>
      <input
        type="number"
        placeholder="Quantity"
        value={quantity}
        onChange={(event) => setQuantity(event.target.value)}
      ></input>
      <input
        type="number"
        placeholder="Unit Value"
        value={unitValue}
        onChange={(event) =>
          setUnitValue(parseFloat(event.target.value).toFixed(2))
        }
      ></input>
      <button
        className="edit"
        onClick={(event) => {
          if (edit) updateItem();
          setEdit(!edit);
        }}
      >
        Save
      </button>
      <button className="delete" onClick={(event) => setEdit(!edit)}>
        Cancel
      </button>
    </div>
  ) : (
    <div className="item">
      <h4 className="field">{id}</h4>
      <h4 className="field">{name}</h4>
      <h4 className="field">{quantity}</h4>
      <h4 className="field">{unitValue}</h4>
      <button className="edit" onClick={(event) => setEdit(!edit)}>
        Edit
      </button>
      <button className="delete">Delete</button>
    </div>
  );
}

Item.propTypes = {
  id: PropTypes.number,
  name: PropTypes.string,
  quantity: PropTypes.number,
  unitValue: PropTypes.number,
};

export default Item;
