import "./App.css";
import { useState } from "react";
import Axios from "axios";
import Item from "./components/Item.jsx";
import InventoryForm from "./components/InventoryForm";

function App() {
  const [itemsList, setItemsList] = useState([]);

  const createItem = (name, quantity, unitValue) => {
    Axios.post("http://localhost:3001/createItem", {
      name: name,
      quantity: quantity,
      unitValue: unitValue,
    })
      .then((response) => {
        getItems();
      })
      .catch((err) => {
        console.log(err.toString());
      });
  };

  const getItems = () => {
    Axios.get("http://localhost:3001/getItems")
      .then((response) => {
        setItemsList(response.data);
      })
      .catch((err) => {
        console.log(err.toString());
      });
  };

  return (
    <div className="App">
      <div className="header">
        <h1>LogistX Inventory Tracker</h1>
      </div>
      <InventoryForm createItem={createItem} />
      <h3 className="subheader">Manage Inventory</h3>
      <div className="divider"></div>
      <div className="inventory">
        <button className="large-button" onClick={getItems}>
          Load Inventory
        </button>
        <div className="columns">
          <h4>Item ID</h4>
          <h4>Name</h4>
          <h4>Quantity</h4>
          <h4>Unit Price</h4>
          <h4>Last Update</h4>
          <h4></h4>
          <h4></h4>
        </div>
        {itemsList.map((val, key) => {
          return (
            <Item
              key={val.id}
              id={val.id}
              name={val.name}
              quantity={val.quantity}
              unitValue={val.unitValue}
              lastUpdate={val.updatedAt.substring(0, 10)}
              refresh={getItems}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
